Handlebars.registerHelper('selectFancyDisplayText', function(currentValue, data) {
    var dataObj = _.findWhere(data,{value:( currentValue ? currentValue.toString() : '')});
    if( dataObj ){
        return $.trim($(ADF.templates.inputHelpers.selectFancyRecord(dataObj)).text());
    }else{
        return null;
    }
});
/*global
Handlebars
*/
Handlebars.registerHelper('selectFancyLink', function(value, label) {
    if( value === label ){
        return new Handlebars.SafeString('<a href="#">'+value+'</a>');
    }else{
        return new Handlebars.SafeString('<a href="#">'+value+': '+label+'</a>');
    }
});